mapper.select:
SELECT A.*, M.nickname AS extra__writer
FROM article AS A
INNER JOIN `Member` AS M
ON M.id = A.memberId
AND A.memberId = #{memberId}
AND A.boardId = #{boardId}
AND A.displayStatus = 1
ORDER BY A.id DESC;

mapper.update:
UPDATE article

	updateDate = NOW(),
	title = #{title},
	body = #{body},
	cateId = #{cateId}

WHERE id = #{id}
