mapper.select:
SELECT * FROM article
WHERE displayStatus = 1
ORDER BY id DESC;

SELECT * FROM article
WHERE displayStatus = 1
AND id = #{id};

SELECT A.*
FROM article AS A
INNER JOIN `Member` AS M
ON M.id = A.memberId
AND A.memberId = #{memberId}
AND A.boardId = #{boardId}
AND A.displayStatus = 1
ORDER BY A.id DESC;

SELECT * FROM article
WHERE displayStatus = 1
AND boardId = #{boardId}
ORDER BY id DESC;

mapper.update:
UPDATE article

	updateDate = NOW(),
	title = #{title},
	body = #{body},
	cateId = #{cateId}

WHERE id = #{id}
