mapper.select:
SELECT * FROM `member`
WHERE loginId = #{loginId}
AND loginPw = #{loginPwReal}
AND delStatus = 0;

mapper.update:
UPDATE `member` SET nickname = #{nickname},
email = #{email}, authStatus = 0,
loginPw = #{newLoginPwReal}, tempPwStatus = 0, updateDate = NOW() WHERE id = #{id} AND delStatus = 0;

